def create_state_plot(clickData, value):
    sphere = go.Surface(z=z, x=x, y=y, opacity=0.2, colorscale='Greys', hoverinfo='skip', showscale=False)
    if not clickData:
        return go.Figure(sphere)
    idx = clickData['points'][0]['pointIndex']
    # vals = bloch_multivector_data(statevec)[idx]
    vals = bloch_multivector_data(states[value])[idx]
    fig2 = go.Figure(data=[sphere, go.Scatter3d(x=[0, vals[0]], y=[0, vals[1]], z=[0, vals[2]], mode='lines',
                                        line={'color': 'darkblue'}),
                           go.Cone(x=[vals[0]], y=[vals[1]], z=[vals[2]], u=[vals[0] * 0.3], v=[vals[1] * 0.3],
                                   w=[vals[2] * 0.3], colorscale='Blues')],
                     layout=go.Layout(scene={'xaxis': {'showspikes': False}, 'yaxis': {'showspikes': False},
                                             'zaxis': {'showspikes': False}}))  # TODO:remove lines

    return fig2

